import React, { Component } from 'react';
import './main.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Link} from 'react-router-dom';
import { Redirect } from 'react-router';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';



class Main extends Component {
    constructor(props){
        super(props);
        this.state = {
            books:[]
        }
    }

    componentDidMount(){
        this.getBooks();
      }

    getBooks = _ => {
        fetch('http://localhost:4000/books')
        .then(res => res.json())
        .then(res => this.setState({books: res.data}))
        .catch(err => console.error(err))
    
    }
    
    renderBooks = ({list_id, title, edition, isbn, price,book_type,book_condition}) => 
        <div className="card-inline" key = {list_id}>
            <MDBCol>
            <MDBCard style={{ width: "22rem" }}>
                 <img src="https://www.qualtrics.com/m/assets/blog/wp-content/uploads/2018/08/shutterstock_1068141515.jpg" className="card-img-top" alt="..."/>

                {/* <MDBCardImage className="img-fluid" src="https://www.qualtrics.com/m/assets/blog/wp-content/uploads/2018/08/shutterstock_1068141515.jpg" waves /> */}
                <MDBCardBody>
                <MDBCardTitle>{title}</MDBCardTitle>
                <MDBCardText>
                    Edition: {edition}, ISBN: {isbn}
                </MDBCardText>
                <ul className="list-group list-group-flush">
                    <li className="list-group-item"> Book Type: {book_type}</li>
                    <li className="list-group-item"> Book Condition: {book_condition}</li>
                    <li className="list-group-item"> Price: ${price}</li>
                </ul>
                <MDBBtn href="#">Contact</MDBBtn>
                </MDBCardBody>
            </MDBCard>
            </MDBCol>

            {/* <div className="card ">
                <img src="https://www.qualtrics.com/m/assets/blog/wp-content/uploads/2018/08/shutterstock_1068141515.jpg" className="card-img-top" alt="..."/>
                <div className="card-body">
                    <h5 className="card-title"> {title}</h5>
                    <p className="card-text"> Edition: {edition}, ISBN: {isbn}  </p>
                </div>
                <ul className="list-group list-group-flush">
                    <li className="list-group-item"> Book Type: {book_type}</li>
                    <li className="list-group-item"> Book Condition: {book_condition}</li>
                    <li className="list-group-item"> Price: $ {price}</li>
                </ul>
                <div className="card-body">
                <button type="button" class="btn btn-primary">Contact</button>
                </div>
            </div>  */}
        </div>

    render(){
        return (
        <div>
            <div>
                {this.state.books.map(this.renderBooks)}    
            </div>
        </div>
        );
    }
}

export default Main;
