import React, { Component } from "react";
// import { Navbar, Nav, Form, FormControl, Button } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import Card from "./Card";
import "./Profile.css";
import Navbar from "../main/navbar/navbar";
// import Sidebar from "../main/sidebar/sidebar";

class Profile extends Component {
  state = {
    user: null,
    major: null,
    profile_picture: null,
    email: null,
    listings: []
  };

  componentDidMount() {
    // fetch(`http://localhost:4000/profile?email=${email}`)
    fetch("http://localhost:4000/profile?email=bill.joe@testemail.com")
      .then(res => res.json())
      .then(data => {
        this.setState({
          user: data[0].username,
          major: "Computer Science B.S.",
          email: data[0].email
        });
        console.log(data);
      });
  }

  render() {
    var { user, major, listings, email, profile_picture } = this.state;

    // if user has no profile picture use default
    if (!profile_picture) {
      profile_picture =
        "https://icon-library.net/images/default-user-icon/default-user-icon-8.jpg";
    }

    return (
      <div>
        <Navbar />
        {/* <Sidebar /> */}
        <div className="Top">
          <header class="page-cover">
            <div class="text-center">
              <img
                src={profile_picture}
                width="140"
                height="140"
                border="0"
                class="rounded-circle"
              />
              <h2 class="h4 mt-2 mb-0">{user}</h2>
              <p class="text-muted">{email}</p>
              <button class="btn btn-primary" type="submit">
                Message
              </button>
            </div>
          </header>
        </div>
        <div class="Inner">
          <nav aria-label="breadcrumb">
            <ol class="breadcrumb">
              <li class="breadcrumb-item active" aria-current="page">
                <strong>{user}'s Active Listings</strong>
              </li>
            </ol>
          </nav>
          <div class="album py-3">
            <div class="container pd-1">
              <div class="row">
                <div class="col-sm-4">
                  <div class="card shadow-sm" style={{ width: "18rem" }}>
                    <img
                      src="https://prodimage.images-bn.com/pimages/9780262033848_p0_v1_s550x406.jpg"
                      class="card-img-top"
                      alt="..."
                    />
                    <div class="card-body">
                      <h5 class="card-title">Introduction to Algorithms</h5>
                      <a href="#" class="btn btn-primary">
                        Inquire
                      </a>
                      <p class="d-inline">$10.00</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );

    const list = listings.map(listing => {
      return <div></div>;
    });
  }
}

export default Profile;
