import React, { Component } from "react";
// import { Navbar, Nav, Form, FormControl, Button } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import Card from "./Card";
import "./Profile.css";
import Navbar from "../main/navbar/navbar";
// import Sidebar from "../main/sidebar/sidebar";

class Profile extends Component {
  state = {
    user: null,
    major: null,
    profile_picture: null,
    email: null,
    listings: []
  };

  componentDidMount() {
    // fetch(`http://localhost:4000/profile?email=${email}`)
    fetch("http://localhost:4000/profile?email=bill.joe@testemail.com")
      .then(res => res.json())
      .then(data => {
        this.setState({
          user: data[0].username,
          major: "Computer Science B.S.",
          email: data[0].email
        });
      });
    fetch("http://localhost:4000/userListings?email=bill.joe@testemail.com")
      .then(res => res.json())
      .then(data => {
        this.setState({
          listings: data
        });
      });

    // Promise.all([
    //   fetch("http://localhost:4000/profile?email=bill.joe@testemail.com"),
    //   fetch("http://localhost:4000/userListings?email=bill.joe@testemail.com")
    // ])
    //   .then(([res1, res2]) => Promise.all([res1.json(), res2.json()]))
    //   .then(([data1, data2]) =>
    //     this.setState({
    //       user: data1[0].username,
    //       email: data1[0].email,
    //       listings: data2
    //     })
    //   );
  }

  render() {
    var { user, major, listings, email, profile_picture } = this.state;

    // if user has no profile picture use default
    if (!profile_picture) {
      profile_picture =
        "https://icon-library.net/images/default-user-icon/default-user-icon-8.jpg";
    }

    const profileListings = listings.map(listing => {
      return (
        <div className="col-md-3 pb-3" key={listing.list_id}>
          <Card bookTitle={listing.title} bookPrice={listing.price} />
        </div>
      );
    });

    return (
      <div>
        <Navbar />
        {/* <Sidebar /> */}
        <div className="Top">
          <header class="page-cover">
            <div class="text-center">
              <img
                src={profile_picture}
                width="140"
                height="140"
                border="0"
                class="rounded-circle"
              />
              <h2 class="h4 mt-2 mb-0">{user}</h2>
              <p class="text-muted">{email}</p>
              <button class="btn btn-primary" type="submit">
                Message
              </button>
            </div>
          </header>
        </div>
        <div class="Inner">
          <nav aria-label="breadcrumb">
            <ol class="breadcrumb">
              <li class="breadcrumb-item active" aria-current="page">
                <strong>{user}'s Active Listings</strong>
              </li>
            </ol>
          </nav>
          {/* <div class="album py-3">
            <div class="container pd-1">
              <div class="row">
                <div class="col-sm-4">{profileListings}</div>
              </div>
            </div>
          </div> */}
          <div class="container pd-1">
            <div class="row">
              <div class="col-sm-11">
                <div class="card-deck">{profileListings}</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Profile;
