import React, { Component } from 'react';
import './main.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Link} from 'react-router-dom';
import { Redirect } from 'react-router';


class Main extends Component {
    constructor(props){
        super(props);
        this.state = {
            books:[]
        }
    }

    componentDidMount(){
        this.getBooks();
      }

    getBooks = _ => {
        fetch('http://localhost:4000/books')
        .then(res => res.json())
        .then(res => this.setState({books: res.data}))
        .catch(err => console.error(err))
    
    }
    
    renderBooks = ({list_id, title, edition, isbn, price,book_type,book_condition}) => <div key = {list_id} >List ID: {list_id} Name: {title}, Edition: {edition} ISBN: {isbn}, Price: {price}, Book Type: {book_type}, Book Condition: {book_condition} </div>

    render(){
        return (
        <div>
            

            Welcome {this.props.location.state.username}, with the email {this.props.location.state.email}
        </div>
        );
    }
}

export default Main;
